package com.topseeker.blob.reader;

import java.io.IOException;
import java.util.ArrayList;
import java.util.List;

import javax.servlet.ServletOutputStream;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.validation.executable.ValidateOnExecution;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Controller;
import org.springframework.ui.ModelMap;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.bind.annotation.RestController;

import com.topseeker.shop.product.model.ShopProductService;
import com.topseeker.shop.productpic.model.ShopProductPicService;
import com.topseeker.shop.productpic.model.ShopProductPicVO;

@Controller
@RequestMapping("/shop")
public class DBGifReaderControllerShop {

	// 商品圖片
	@Autowired
	ShopProductPicService ShopProductPicSvc;

	// 商品
//	@Autowired
//	ShopProductService ShopProductSvc;

	// 圖片讀取
	@GetMapping("DBGifReader")
	public void dBGifReader(@RequestParam("prodNo") String prodNo, HttpServletRequest req, HttpServletResponse res)
			throws IOException {
		res.setContentType("image/gif");
		ServletOutputStream out = res.getOutputStream();

		try {
			List<ShopProductPicVO> rtn = new ArrayList<>();
			rtn = ShopProductPicSvc.getOneShopProductPic(Integer.valueOf(prodNo));

			for (ShopProductPicVO VO : rtn) {
				System.out.println(prodNo+" : "+VO.getProdPic());
				out.write(VO.getProdPic());

			}
			// out.write(ShopProductPicSvc.getOneShopProductPic(Integer.valueOf(prodNo)).getProdPic());

		} catch (Exception e) {
			byte[] buf = java.util.Base64.getDecoder().decode(
					"");
			out.write(buf);
			// e.printStackTrace();
		}

	}

	// 測試第二種寫法
	@GetMapping("DBGifReader2")
	public void dBGifReader2(@RequestParam("prodNo") String prodNo, HttpServletRequest req, HttpServletResponse res)
			throws IOException {
		res.setContentType("image/gif");
		ServletOutputStream out = res.getOutputStream();

		try {
			out.write(ShopProductPicSvc.getFirstShopProductPicVO(Integer.valueOf(prodNo)).getProdPic());

		} catch (Exception e) {
			byte[] buf = java.util.Base64.getDecoder().decode(
					"");
			out.write(buf);
			e.printStackTrace();
		}

	}

	// 多張圖片讀寫
	@GetMapping("DBGifReader3")
	public void dBGifReader3(@RequestParam("prodPicNo") String prodPicNo, HttpServletRequest req,
			HttpServletResponse res) throws IOException {
		res.setContentType("image/gif");
		ServletOutputStream out = res.getOutputStream();

		try {
			List<ShopProductPicVO> rtn = new ArrayList<>();
			rtn = ShopProductPicSvc.getAllProductPic(Integer.valueOf(prodPicNo));

			for (ShopProductPicVO VO : rtn) {
				System.out.println(prodPicNo+" : "+VO.getProdPic());
				out.write(VO.getProdPic());

			}
		} catch (Exception e) {
			byte[] buf = java.util.Base64.getDecoder().decode(
					"");
			out.write(buf);
			e.printStackTrace();
		}

	}

}